sys_ui_policy.script_false:
function onCondition() {
  var isFnL = g_user.hasRole('x_mast4_new_user_a.F&Lteam');
    if (isFnL) {
        g_form.setReadonly('u_user_added_in_group', true);
        g_form.setReadonly('u_login_in_mastek_domain', true);
        g_form.setReadonly('u_local_domain', true);
        g_form.setReadonly('u_local_login', true);
        g_form.setReadonly('u_domain_login', true);
        g_form.setReadOnly('u_added_encryption', true);
    }
}

sys_ui_policy.script_true:
function onCondition() {
  var user = g_user.hasRole('x_mast4_new_user_a.DCThelp desk');
    if (user) {
        g_form.setReadonly('u_user_added_in_group', false);
        g_form.setReadonly('u_login_in_mastek_domain', false);
        g_form.setReadonly('u_local_domain', false);
        g_form.setReadonly('u_local_login', false);
        g_form.setReadonly('u_domain_login', false);
        g_form.setReadOnly('u_added_encryption', false);
    }
}
