sysevent_in_email_action.script:
(function runAction( /*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

    var number1 = email.subject.substr(3, 13);
    gs.info('number1:' + number1);
    var number2 = email.subject.substr(3, 30);
    var sub = number2.toString();
    gs.info('subject' + sub);
    gs.info('allocation number=' + number1 + " ", 'Running');
    var approver = new GlideRecord('x_mast4_new_user_a_new_user_asset_allocation');
    approver.addQuery('number', number1);
    gs.info("Query " + " " + approver.number);
    approver.query();
    gs.info("Confirmation runninng");
    gs.info("Query is Running ");
    while (approver.next()) {
        gs.info("inside while");

        // 		gs.info("index"+email.subject.indexOf());
        if (email.subject.toLowerCase().indexOf("received") > -1) {
            //current.approval = 'approved';
            gs.info('inside if received');
            approver.u_stage = 'u_delivered_confirm';
            approver.update();
            //current.update();
        } else if (email.subject.toLowerCase().indexOf("reject") > -1) {
            //current.approval = 'rejected';
            gs.info('inside if reject');
            approver.u_stage = 'u_cancelled_reverse';
            approver.update();
            //current.update();
        }
    }
    current.update();
    sys_email.target_table = 'x_mast4_new_user_a_new_user_asset_allocation';
    gs.info("Confirmation mails");
})(current, event, email, logger, classifier);
